import{_ as a,o as e,c as t,U as l}from"./chunks/framework.316ef557.js";const _=JSON.parse('{"title":"Html 问题汇总","description":"","frontmatter":{},"headers":[],"relativePath":"notes/html/html汇总.md","filePath":"notes/html/html汇总.md","lastUpdated":1690725674000}'),i={name:"notes/html/html汇总.md"},r=l('<h1 id="html-问题汇总" tabindex="-1">Html 问题汇总 <a class="header-anchor" href="#html-问题汇总" aria-label="Permalink to &quot;Html&amp;nbsp;问题汇总&quot;">​</a></h1><h2 id="如何理解-html-中的语义化标签" tabindex="-1">如何理解 HTML 中的语义化标签 <a class="header-anchor" href="#如何理解-html-中的语义化标签" aria-label="Permalink to &quot;如何理解 HTML 中的语义化标签&quot;">​</a></h2><h3 id="是什么" tabindex="-1">是什么 <a class="header-anchor" href="#是什么" aria-label="Permalink to &quot;是什么&quot;">​</a></h3><p>语义化标签是一种写 HTML 标签的方法论/方式。</p><h3 id="怎么做" tabindex="-1">怎么做 <a class="header-anchor" href="#怎么做" aria-label="Permalink to &quot;怎么做&quot;">​</a></h3><p>实现方法是遇到标题就用 h1 到 h6，遇到段落用 p，遇到文章用 article，主要内容用 main，边栏用 aside，导航用 nav……（就是找到中文对应的英文）</p><h3 id="解决了什么问题" tabindex="-1">解决了什么问题 <a class="header-anchor" href="#解决了什么问题" aria-label="Permalink to &quot;解决了什么问题&quot;">​</a></h3><p>明确了 HTML 的书写规范</p><h3 id="优点" tabindex="-1">优点 <a class="header-anchor" href="#优点" aria-label="Permalink to &quot;优点&quot;">​</a></h3><ol><li>适合搜索引擎检索；</li><li>适合人类阅读，利于团队维护。</li></ol><h3 id="缺点" tabindex="-1">缺点 <a class="header-anchor" href="#缺点" aria-label="Permalink to &quot;缺点&quot;">​</a></h3><p>没有。</p><h3 id="怎么解决缺点" tabindex="-1">怎么解决缺点 <a class="header-anchor" href="#怎么解决缺点" aria-label="Permalink to &quot;怎么解决缺点&quot;">​</a></h3><p>无需解决。</p><h2 id="html-5-有哪些新标签" tabindex="-1">HTML 5 有哪些新标签？ <a class="header-anchor" href="#html-5-有哪些新标签" aria-label="Permalink to &quot;HTML 5 有哪些新标签？&quot;">​</a></h2><ul><li><p>文章相关：header main footer nav section article figure mark</p></li><li><p>多媒体相关：video audio svg canvas</p></li><li><p>表单相关：type=email type=tel</p></li></ul><p>MDN 把所有标签都列在<a href="https://developer.mozilla.org/zh-CN/docs/Web/HTML/Element" target="_blank" rel="noreferrer">这里</a>了，且有教程</p><h2 id="canvas-和-svg-的区别是什么" tabindex="-1">Canvas 和 SVG 的区别是什么？ <a class="header-anchor" href="#canvas-和-svg-的区别是什么" aria-label="Permalink to &quot;Canvas 和 SVG 的区别是什么？&quot;">​</a></h2><ul><li><p>Canvas 主要是用笔刷来绘制 2D 图形的。</p></li><li><p>SVG 主要是用标签来绘制不规则矢量图的。</p></li><li><p>相同点：都是主要用来画 2D 图形的。</p></li><li><p>不同点：Canvas 画的是位图，SVG 画的是矢量图。</p></li><li><p>不同点：SVG 节点过多时渲染慢，Canvas 性能更好一点，但写起来更复杂。</p></li><li><p>不同点：SVG 支持分层和事件，Canvas 不支持，但是可以用库实现。</p></li></ul>',19),o=[r];function h(n,s,d,p,c,m){return e(),t("div",null,o)}const b=a(i,[["render",h]]);export{_ as __pageData,b as default};
