import{_ as a,o as s,c as l,U as n}from"./chunks/framework.316ef557.js";const F=JSON.parse('{"title":"HTML的元素和分类","description":"","frontmatter":{},"headers":[],"relativePath":"notes/html/元素和分类.md","filePath":"notes/html/元素和分类.md","lastUpdated":1689776313000}'),o={name:"notes/html/元素和分类.md"},t=n('<h1 id="html的元素和分类" tabindex="-1">HTML的元素和分类 <a class="header-anchor" href="#html的元素和分类" aria-label="Permalink to &quot;HTML的元素和分类&quot;">​</a></h1><h2 id="行内元素-内联元素" tabindex="-1">行内元素（内联元素） <a class="header-anchor" href="#行内元素-内联元素" aria-label="Permalink to &quot;行内元素（内联元素）&quot;">​</a></h2><h3 id="特点" tabindex="-1">特点 <a class="header-anchor" href="#特点" aria-label="Permalink to &quot;特点&quot;">​</a></h3><ol><li>和其他元素都在同一行上</li><li>高，行高及外边距和内边距不可改变</li><li>宽度就是它的文字或图片的宽度，不可改变</li><li>行内元素只能容纳文本或者其他行内元素</li><li>设置行内元素，需要注意如下</li></ol><p>（1）设置宽度 width 无效。 （2）设置高度 height 无效，但可以通过 line-height 来设置。 （3）设置 margin 只有 左右有效，上下无效。 （4）设置 padding 只有 左右有效，上下无效。注意元素范围是增大了，但是对元素周围的内容是没影响的。</p><div class="language-html"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">span</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">span</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">，</span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">a</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">a</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">,</span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">strong</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">和</span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">em</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">就是典型的行内元素inline元素</span></span></code></pre></div><h2 id="块级元素" tabindex="-1">块级元素 <a class="header-anchor" href="#块级元素" aria-label="Permalink to &quot;块级元素&quot;">​</a></h2><h3 id="特点-1" tabindex="-1">特点 <a class="header-anchor" href="#特点-1" aria-label="Permalink to &quot;特点&quot;">​</a></h3><ol><li>总是在新一行开始</li><li>高度，行高以及外边距和内边距都可改变</li><li>若宽度没有设置，则默认宽度为容器的100%，除非设定一个宽度</li><li>块级元素可以容纳行内元素和其他块级元素</li></ol><div class="language-html"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">div</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">,</span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">p</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">,</span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">h1</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">,</span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">from</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">，</span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">ul</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">,</span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">li</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">这些都是典型的块级元素 block元素</span></span></code></pre></div><h2 id="行内块元素" tabindex="-1">行内块元素 <a class="header-anchor" href="#行内块元素" aria-label="Permalink to &quot;行内块元素&quot;">​</a></h2><h3 id="特点-2" tabindex="-1">特点 <a class="header-anchor" href="#特点-2" aria-label="Permalink to &quot;特点&quot;">​</a></h3><p>行内块元素（inline-block）就是同时具备内联元素和块状元素的特点 换其它元素都在一行上 元素的高度和宽度，汉高以及底部，顶部边距都可以设置</p><div class="language-html"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">img</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">,</span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">input</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">标签就是这种内联块元素标签</span></span></code></pre></div><h2 id="行内元素和块级元素的转换" tabindex="-1">行内元素和块级元素的转换 <a class="header-anchor" href="#行内元素和块级元素的转换" aria-label="Permalink to &quot;行内元素和块级元素的转换&quot;">​</a></h2><h3 id="display" tabindex="-1">display <a class="header-anchor" href="#display" aria-label="Permalink to &quot;display&quot;">​</a></h3><p>（1）行内元素 display:inline （2）块级元素 display:block （3）行内块元素 display:inline-block （4）行内元素转换块级元素 display:block （5）块级元素转换行内元素 display:inline</p><h2 id="" tabindex="-1"><a class="header-anchor" href="#" aria-label="Permalink to &quot;&quot;">​</a></h2>',18),e=[t];function p(r,i,c,h,d,y){return s(),l("div",null,e)}const m=a(o,[["render",p]]);export{F as __pageData,m as default};
